package bean;

public class linhvucbean {

  private String malv;
  private String tenlv;

  public linhvucbean() {
    super();
    // TODO Auto-generated constructor stub
  }

  public linhvucbean(String malv, String tenlv) {
    super();
    this.malv = malv;
    this.tenlv = tenlv;
  }

  public String getMalv() {
    return malv;
  }

  public void setMalv(String malv) {
    this.malv = malv;
  }

  public String getTenlv() {
    return tenlv;
  }

  public void setTenlv(String tenlv) {
    this.tenlv = tenlv;
  }



}
